import { 
  Task, InsertTask, 
  WaterIntake, InsertWaterIntake,
  Exercise, InsertExercise,
  WorkoutType, InsertWorkoutType,
  MindActivity, InsertMindActivity,
  RoutineItem, InsertRoutineItem,
  Goal, InsertGoal,
  Performance, InsertPerformance,
  Streak, InsertStreak,
  tasksTable,
  waterIntakeTable,
  exercisesTable,
  workoutTypesTable,
  mindActivitiesTable,
  routineItemsTable,
  goalsTable,
  performanceTable,
  streakTable
} from "@shared/schema";
import { eq, and } from 'drizzle-orm';
import { getDb } from './db';
import { IStorage } from './storage';

export class DatabaseStorage implements IStorage {
  private db = getDb();

  // Tasks
  async getTasks(date?: string): Promise<Task[]> {
    if (date) {
      return await this.db.select().from(tasksTable).where(eq(tasksTable.date, date));
    }
    return await this.db.select().from(tasksTable);
  }

  async getTask(id: string): Promise<Task | undefined> {
    const result = await this.db.select().from(tasksTable).where(eq(tasksTable.id, id));
    return result[0];
  }

  async createTask(task: InsertTask): Promise<Task> {
    const result = await this.db.insert(tasksTable).values(task).returning();
    return result[0];
  }

  async updateTask(id: string, task: Partial<Task>): Promise<Task> {
    const result = await this.db.update(tasksTable).set(task).where(eq(tasksTable.id, id)).returning();
    if (result.length === 0) throw new Error("Task not found");
    return result[0];
  }

  async deleteTask(id: string): Promise<void> {
    await this.db.delete(tasksTable).where(eq(tasksTable.id, id));
  }

  // Water Intake
  async getWaterIntake(date: string): Promise<WaterIntake | undefined> {
    const result = await this.db.select().from(waterIntakeTable).where(eq(waterIntakeTable.date, date));
    return result[0];
  }

  async createOrUpdateWaterIntake(waterIntake: InsertWaterIntake): Promise<WaterIntake> {
    const existing = await this.getWaterIntake(waterIntake.date);

    if (existing) {
      const result = await this.db.update(waterIntakeTable)
        .set(waterIntake)
        .where(eq(waterIntakeTable.date, waterIntake.date))
        .returning();
      return result[0];
    } else {
      const result = await this.db.insert(waterIntakeTable).values(waterIntake).returning();
      return result[0];
    }
  }

  // Exercises
  async getExercises(date?: string, isWeekly?: boolean): Promise<Exercise[]> {
    let query = this.db.select().from(exercisesTable);

    const conditions = [];
    if (date) conditions.push(eq(exercisesTable.date, date));
    if (isWeekly !== undefined) conditions.push(eq(exercisesTable.isWeekly, isWeekly));

    if (conditions.length > 0) {
      query = query.where(and(...conditions));
    }

    return await query;
  }

  async getExercise(id: string): Promise<Exercise | undefined> {
    const result = await this.db.select().from(exercisesTable).where(eq(exercisesTable.id, id));
    return result[0];
  }

  async createExercise(exercise: InsertExercise): Promise<Exercise> {
    const result = await this.db.insert(exercisesTable).values(exercise).returning();
    return result[0];
  }

  async updateExercise(id: string, exercise: Partial<Exercise>): Promise<Exercise> {
    const result = await this.db.update(exercisesTable).set(exercise).where(eq(exercisesTable.id, id)).returning();
    if (result.length === 0) throw new Error("Exercise not found");
    return result[0];
  }

  async deleteExercise(id: string): Promise<void> {
    await this.db.delete(exercisesTable).where(eq(exercisesTable.id, id));
  }

  // Workout Types
  async getWorkoutTypes(): Promise<WorkoutType[]> {
    return await this.db.select().from(workoutTypesTable);
  }

  async createWorkoutType(workoutType: InsertWorkoutType): Promise<WorkoutType> {
    const result = await this.db.insert(workoutTypesTable).values(workoutType).returning();
    return result[0];
  }

  async deleteWorkoutType(id: string): Promise<void> {
    await this.db.delete(workoutTypesTable).where(eq(workoutTypesTable.id, id));
  }

  // Mind Activities
  async getMindActivities(date?: string): Promise<MindActivity[]> {
    if (date) {
      return await this.db.select().from(mindActivitiesTable).where(eq(mindActivitiesTable.date, date));
    }
    return await this.db.select().from(mindActivitiesTable);
  }

  async getMindActivity(id: string): Promise<MindActivity | undefined> {
    const result = await this.db.select().from(mindActivitiesTable).where(eq(mindActivitiesTable.id, id));
    return result[0];
  }

  async createMindActivity(activity: InsertMindActivity): Promise<MindActivity> {
    const result = await this.db.insert(mindActivitiesTable).values(activity).returning();
    return result[0];
  }

  async updateMindActivity(id: string, activity: Partial<MindActivity>): Promise<MindActivity> {
    const result = await this.db.update(mindActivitiesTable).set(activity).where(eq(mindActivitiesTable.id, id)).returning();
    if (result.length === 0) throw new Error("Mind activity not found");
    return result[0];
  }

  async deleteMindActivity(id: string): Promise<void> {
    await this.db.delete(mindActivitiesTable).where(eq(mindActivitiesTable.id, id));
  }

  // Routine Items
  async getRoutineItems(type?: string, date?: string): Promise<RoutineItem[]> {
    let query = this.db.select().from(routineItemsTable);

    const conditions = [];
    if (type) conditions.push(eq(routineItemsTable.type, type));
    if (date) conditions.push(eq(routineItemsTable.date, date));

    if (conditions.length > 0) {
      query = query.where(and(...conditions));
    }

    return await query;
  }

  async getRoutineItem(id: string): Promise<RoutineItem | undefined> {
    const result = await this.db.select().from(routineItemsTable).where(eq(routineItemsTable.id, id));
    return result[0];
  }

  async createRoutineItem(item: InsertRoutineItem): Promise<RoutineItem> {
    const result = await this.db.insert(routineItemsTable).values(item).returning();
    return result[0];
  }

  async updateRoutineItem(id: string, item: Partial<RoutineItem>): Promise<RoutineItem> {
    const result = await this.db.update(routineItemsTable).set(item).where(eq(routineItemsTable.id, id)).returning();
    if (result.length === 0) throw new Error("Routine item not found");
    return result[0];
  }

  async deleteRoutineItem(id: string): Promise<void> {
    await this.db.delete(routineItemsTable).where(eq(routineItemsTable.id, id));
  }

  // Goals
  async getGoals(type?: string): Promise<Goal[]> {
    if (type) {
      return await this.db.select().from(goalsTable).where(eq(goalsTable.type, type));
    }
    return await this.db.select().from(goalsTable);
  }

  async getGoal(id: string): Promise<Goal | undefined> {
    const result = await this.db.select().from(goalsTable).where(eq(goalsTable.id, id));
    return result[0];
  }

  async createGoal(goal: InsertGoal): Promise<Goal> {
    const result = await this.db.insert(goalsTable).values(goal).returning();
    return result[0];
  }

  async updateGoal(id: string, goal: Partial<Goal>): Promise<Goal> {
    const result = await this.db.update(goalsTable).set(goal).where(eq(goalsTable.id, id)).returning();
    if (result.length === 0) throw new Error("Goal not found");
    return result[0];
  }

  async deleteGoal(id: string): Promise<void> {
    await this.db.delete(goalsTable).where(eq(goalsTable.id, id));
  }

  // Performance
  async getPerformance(date?: string): Promise<Performance[]> {
    if (date) {
      return await this.db.select().from(performanceTable).where(eq(performanceTable.date, date));
    }
    return await this.db.select().from(performanceTable);
  }

  async createOrUpdatePerformance(performance: InsertPerformance): Promise<Performance> {
    const existing = await this.db.select().from(performanceTable)
      .where(eq(performanceTable.date, performance.date));

    if (existing.length > 0) {
      const result = await this.db.update(performanceTable)
        .set(performance)
        .where(eq(performanceTable.id, existing[0].id))
        .returning();
      return result[0];
    } else {
      const result = await this.db.insert(performanceTable).values(performance).returning();
      return result[0];
    }
  }

  // Streak
  async getStreak(): Promise<Streak> {
    const result = await this.db.select().from(streakTable).limit(1);
    if (result.length === 0) {
      // Create default streak if none exists
      const defaultStreak = { id: "default", current: 0, highest: 0 };
      const created = await this.db.insert(streakTable).values(defaultStreak).returning();
      return created[0];
    }
    return result[0];
  }

  async updateStreak(streak: Partial<Streak>): Promise<Streak> {
    const existing = await this.getStreak();
    const result = await this.db.update(streakTable)
      .set(streak)
      .where(eq(streakTable.id, existing.id))
      .returning();
    return result[0];
  }
}

server/db.ts.content:
import { drizzle } from 'drizzle-orm/neon-serverless';
import { neon } from '@neondatabase/serverless';
import { tasksTable } from '@shared/schema';

// Create database connection
let connection: ReturnType<typeof neon> | null = null;
let db: ReturnType<typeof drizzle> | null = null;

export function getDb() {
  if (!db) {
    if (!process.env.DATABASE_URL) {
      throw new Error('DATABASE_URL environment variable is required');
    }

    connection = neon(process.env.DATABASE_URL);
    db = drizzle(connection);
  }

  return db;
}

export async function initializeDatabase() {
  try {
    const database = getDb();
    // Test the connection
    await database.select().from(tasksTable).limit(1);
    console.log('‚úÖ Database connection established');
    return true;
  } catch (error) {
    console.log('‚ùå Database connection failed:', error);
    return false;
  }
}

