#!/usr/bin/env node

// Production startup script for Render
process.env.NODE_ENV = 'production';

console.log('üöÄ Starting Meta Build in production...');
console.log('üìç Current directory:', process.cwd());
console.log('üìÇ Files in directory:', require('fs').readdirSync('.'));

try {
  // Check if the built file exists
  const fs = require('fs');
  const path = require('path');

  const builtFile = path.join(process.cwd(), 'dist', 'index.js');
  console.log('üîç Looking for built file at:', builtFile);

  if (fs.existsSync(builtFile)) {
    console.log('‚úÖ Built file found, starting server...');
    require('./dist/index.js');
  } else {
    console.log('‚ùå Built file not found, trying direct server start...');
    console.log('üìÅ Contents of dist directory:');
    if (fs.existsSync('dist')) {
      console.log(fs.readdirSync('dist'));
    } else {
      console.log('No dist directory found');
    }

    // Fall back to running from source
    console.log('üîÑ Falling back to tsx server...');
    const { spawn } = require('child_process');
    const server = spawn('npx', ['tsx', 'server/index.ts'], {
      stdio: 'inherit',
      env: { ...process.env, NODE_ENV: 'production' }
    });

    server.on('error', (err) => {
      console.error('‚ùå Server error:', err);
      process.exit(1);
    });
  }
} catch (error) {
  console.error('üí• Startup error:', error);
  process.exit(1);
}
